// src/App.jsx
import React, { useState, useEffect } from 'react';
import Login from './components/Login';
import Hotbar from './components/Hotbar';
import HomePage from './components/HomePage'; // Import the HomePage component
import PostPage from './components/PostPage';
import fillerPosts from './fillerPosts.json'; // Import filler posts for testing
import './App.css';
import logo from './components/GatorFoundLogo.png';

const App = () => {
    const [isLoggedIn, setIsLoggedIn] = useState(false); // Simulate login state
    const [view, setView] = useState('home'); // Default view is the home page
    const [lostPosts, setLostPosts] = useState([]); // Initial lost posts
    const [foundPosts, setFoundPosts] = useState([]); // Initial found posts
    const [userPosts, setUserPosts] = useState([]); // User-created posts

    useEffect(() => {
        const fetchPosts = async() => {
        // Fetch all items from the backend
        fetch('http://localhost:5000/GatorFound/items', {
            method: 'GET', // Explicitly specifying the method
            headers: {
                'Content-Type': 'application/json',
            }
        })
            .then((response) => response.json())
            .then((data) => {
                setUserPosts(data.data);  // Store fetched items
                 // Stop loading once data is fetched
            })
            .catch((error) => {
                console.error('Error fetching items:', error);
                  // Stop loading on error
            })
            
        };
        fetchPosts();
    }, []);  // Empty dependency array means this effect runs once after the initial render
    
    useEffect(() => {
        // Filter lost and found posts whenever fillerPosts change
        setLostPosts(userPosts.filter(post => post.postType === 'lost'));
        setFoundPosts(userPosts.filter(post => post.postType === 'found'));
    }, [userPosts]); 

    // Handle creating a new post
    const handleNewPost = (newPost) => {
        setUserPosts([...userPosts, newPost]);

        if (newPost.postType === 'lost') {
            setLostPosts([...lostPosts, newPost]);
        } else if (newPost.postType === 'found') {
            setFoundPosts([...foundPosts, newPost]);
        }
    };

    // If not logged in, show the login page
    if (!isLoggedIn) {
        return <Login setIsLoggedIn={setIsLoggedIn} />;
    }

    return (
        <div className="App">
            <Hotbar setView={setView} />
            {view === 'home' && <HomePage />} {/* Render HomePage when view is home */}
            {(view === 'lost' || view === 'found' || view === 'yourPosts' || view === 'createLostPost' || view === 'createFoundPost') && (
                <PostPage
                    view={view}
                    lostPosts={lostPosts}
                    foundPosts={foundPosts}
                    userPosts={userPosts}
                    handleNewPost={handleNewPost}
                />
            )}
        </div>
    );
};

export default App;
