// src/components/CommentSection.jsx
import React, { useState } from 'react';
import Comment from './Comment';
import './CommentSection.css'; // Ensure CSS is imported

const CommentSection = ({itemID, comments = [], handleCommentSubmit }) => { // Default to empty array
    const [newComment, setNewComment] = useState('');

    const handleNewComment = async (comment) => {
        try {
            console.log("Comment is: ", comment);
            const response = await fetch(`http://localhost:5000/GatorFound/comments/${itemID}/comments`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'auth-token': `${sessionStorage.getItem('token')}`,
                },
                body: JSON.stringify({text: comment}),
            });
            
            if (!response.ok) {
                throw new Error('Failed to add comment');
            }
            console.log("This item is: ",itemID);

            const updatedComments = await response.json();
            console.log("Responses: ", updatedComments.message);
            //setComments(updatedComments); // Update comments after adding the new one
            handleCommentSubmit(updatedComments);
        } catch (error) {
            console.error('Error adding comment:', error);
        }
    };
    const handleAddComment = () => {
        if (newComment.trim()) {
            const commentData = { id: Date.now(), text: newComment, replies: [] };
            handleNewComment(commentData); // Pass new comment back to PostItem
            setNewComment(''); // Clear input
        }
    };

    return (
        <div className="comment-section">
            <div className="comment-form">
                <input
                    type="text"
                    value={newComment}
                    onChange={(e) => setNewComment(e.target.value)}
                    placeholder="Add a comment..."
                />
                <button onClick={handleAddComment}>Post</button>
            </div>

            <div className="comments-list">
                {comments.map(comment => (
                    <Comment key={comment.id} comment={comment} />
                ))}
            </div>
        </div>
    );
};

export default CommentSection;
